mapper.select:
    	SELECT USER_ID, BOOK_ID, BOOK_TITLE
			FROM BOOK_PLAN_TABLE
			WHERE USER_ID = #{user_id}
			  AND BOOK_ID = #{book_id};

mapper.update:
    	UPDATE BOOK_PLAN_TABLE
			SET BOOK_TITLE = #{book_name}
			WHERE USER_ID = #{user_id}
			  AND BOOK_ID = #{book_id};

    	UPDATE BOOK_PLAN_TABLE
			SET DELETE_FLAG = 1
			WHERE USER_ID = #{user_id}
			  AND BOOK_ID = #{book_id}
			  AND DELETE_FLAG = 0 ;

    	UPDATE BOOK_PLAN_TABLE
			SET READ_FLAG = 1
			WHERE USER_ID = #{user_id}
			  AND BOOK_ID = #{book_id}
			  AND READ_FLAG = 0 ;
